mapper.insert:
INSERT INTO animal(
	mm_ifmmSeq
	, ifamName
	, ifamAge
	, ifamKind
	, ifamGender
	, ifamChipNY
	, ifamVaccinateNY
)
VALUES(
	#{ifmmSeq}
	, #{ifamName}
	, #{ifamAge}
	, #{ifamKind}
	, #{ifamGender}
	, #{ifamChipNY}
	, #{ifamVaccinateNY}
)

mapper.update:
UPDATE animal
SET
	 ifamGender	= #{ifamGender}
	,ifamName	= #{ifamName}
	,ifamAge	= #{ifamAge}
	,ifamKind	= #{ifamKind}
	,ifamChipNY	= #{ifamChipNY}
	,mm_ifmmSeq	= #{ifmmSeq}
WHERE 1=1
AND ifamSeq =#{ifamSeq}

UPDATE petUploaded
SET	
	type			= #{type}
	,defaultNy		= #{defaultNy}
	,sort			= #{sort}
	,path			= #{path}
	,originalName	= #{originalName}
	,uuidName		= #{uuidName}
	,ext			= #{ext}
	,size			= #{size}
	,delNy			= 0
WHERE 1=1
AND pseq = #{ifamSeq}
