mapper.insert:
INSERT INTO findpet (
	iffpName
	,iffpBreed
	,iffpGender
	,iffpAnimalShelterzipCode
	,iffpAnimalShelter1
	,iffpAnimalShelter2
	,iffpSpotPlace
	,iffpSpotDate
	,iffpSpotLocation
	,iffpCharacteristic
	,iffpDelNY
	,iffpRegDate
	,mm_ifmmSeq
)
VALUE (
	#{iffpName}
	,#{iffpBreed}
	,#{iffpGender}
	,#{iffpAnimalShelterzipCode}
	,#{iffpAnimalShelter1}
	,#{iffpAnimalShelter2}
	,#{iffpSpotPlace}
	,#{iffpSpotDate}
	,#{iffpSpotLocation}
	,#{iffpCharacteristic}
	,0
	,now()
	,#{ifmmSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE findpet
SET
	 iffpName					= #{iffpName}
	,iffpBreed					= #{iffpBreed}
	,iffpGender					= #{iffpGender}
	,iffpAnimalShelterzipCode	= #{iffpAnimalShelterzipCode}
	,iffpAnimalShelter1			= #{iffpAnimalShelter1}
	,iffpAnimalShelter2			= #{iffpAnimalShelter2}
	,iffpSpotPlace				= #{iffpSpotPlace}
	,iffpSpotDate				= #{iffpSpotDate}
	,iffpSpotLocation			= #{iffpSpotLocation}
	,iffpDelNY					= 0
	,iffpModDate				= now()
WHERE 1=1
AND iffpSeq = #{iffpSeq}

UPDATE abandonedUploaded
SET	
	type			= #{type}
	,defaultNy		= #{defaultNy}
	,sort			= #{sort}
	,path			= #{path}
	,originalName	= #{originalName}
	,uuidName		= #{uuidName}
	,ext			= #{ext}
	,size			= #{size}
	,delNy			= 0
WHERE 1=1
AND pseq = #{iffpSeq}

mapper.insert:
INSERT INTO comment (
