mapper.delete:

DELETE FROM follow
WHERE iffwFollow = #{iffwFollow}
AND iffwFollower = #{iffwFollower}

mapper.select:

SELECT
	COUNT(*)
FROM follow
WHERE iffwFollower = #{iffwFollower}
AND iffwFollow = #{iffwFollow}

SELECT COUNT(*)
FROM follow
WHERE iffwFollow = #{target}

SELECT COUNT(*)
FROM follow
WHERE iffwFollower = #{target}
