mapper.sql:
FROM member a
WHERE 1=1
ORDER BY a.ifmmSeq DESC

AND a.ifmmDelNy = #{shDelNy}
AND a.ifmmUseNy = #{shUseNy}


	AND a.ifmmRegDate BETWEEN #{shstartDate} AND #{shendDate}



	AND a.ifmmName LIKE CONCAT('%',#{shValue},'%')
	AND a.ifmmNickname LIKE CONCAT('%',#{shValue},'%')
	AND a.ifmmAddr1 LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO member (
	ifmmName
	, ifmmZipcode
	, ifmmAddr1
	, ifmmAddr2
	, ifmmAddr3
	, ifmmID
	, ifmmPW
	, ifmmNickname
	, ifmmTelecom
	, ifmmTel
	, ifmmEmail
	, ifmmEmailID
	, ifmmEmailDomain
	, ifmmRegDate
	, ifmmDelNy
)
VALUES (
	#{ifmmName}
	, #{ifmmZipcode}
	, #{ifmmAddr1}
	, #{ifmmAddr2}
	, #{ifmmAddr3}
	, #{ifmmID}
	, #{ifmmPW}
	, #{ifmmNickname}
	, #{ifmmTelecom}
	, #{ifmmTel}
	, #{ifmmEmail}
	, #{ifmmEmailID}
	, #{ifmmEmailDomain}
	, now()
	, 0
)


	SELECT last_insert_id()

mapper.select:
SELECT ifmmSeq , ifmmID FROM member WHERE 1=1 AND ifmmID = #{ifmmID}
AND ifmmPW = #{ifmmPW}
AND ifmmDelNy = 0

mapper.update:
UPDATE member
SET
	  ifmmName			=	#{ifmmName}
	, ifmmZipcode		=	#{ifmmZipcode}
	, ifmmAddr1			=	#{ifmmAddr1}
	, ifmmAddr2			=	#{ifmmAddr2}
	, ifmmAddr3			=	#{ifmmAddr3}
	, ifmmNickname		=	#{ifmmNickname}
	, ifmmTelecom		=	#{ifmmTelecom}
	, ifmmTel			=	#{ifmmTel}
	, ifmmEmail			=   CONCAT(#{ifmmEmailID},

								'@naver.com')
								'@gmail.com')
								'@hanmail.com')
								'@nate.com')
								'@daum.net')

	, ifmmEmailID		=	#{ifmmEmailID}
	, ifmmEmailDomain	=	#{ifmmEmailDomain}
	, ifmmIntroduction	=	#{ifmmIntroduction}
	, ifmmModDate		=	now()
WHERE 1=1
AND ifmmSeq = #{ifmmSeq}

UPDATE memberUploaded
SET	
	type			= #{type}
	,defaultNy		= #{defaultNy}
	,sort			= #{sort}
	,path			= #{path}
	,originalName	= #{originalName}
	,uuidName		= #{uuidName}
	,ext			= #{ext}
	,size			= #{size}
	,delNy			= 0

WHERE 1=1
AND pseq = #{ifmmSeq}
