mapper.insert:
INSERT INTO member (
	ifmmName
	, ifmmZipcode
	, ifmmAddr1
	, ifmmAddr2
	, ifmmAddr3
	, ifmmID
	, ifmmPW
	, ifmmNickname
	, ifmmTelecom
	, ifmmTel
	, ifmmEmail
	, ifmmEmailID
	, ifmmEmailDomain
	, ifmmRegDate
	, ifmmDelNy
)
VALUES (
	#{ifmmName}
	, #{ifmmZipcode}
	, #{ifmmAddr1}
	, #{ifmmAddr2}
	, #{ifmmAddr3}
	, #{ifmmID}
	, #{ifmmPW}
	, #{ifmmNickname}
	, #{ifmmTelecom}
	, #{ifmmTel}
	, #{ifmmEmail}
	, #{ifmmEmailID}
	, #{ifmmEmailDomain}
	, now()
	, 0
)


	SELECT last_insert_id()

INSERT INTO animal(
	mm_ifmmSeq
	, ifamName
	, ifamAge
	, ifamKind
	, ifamGender
	, ifamChipNY
	, ifamVaccinateNY
)
VALUES(
	#{ifmmSeq}
	, #{ifamName}
	, #{ifamAge}
	, #{ifamKind}
	, #{ifamGender}
	, #{ifamChipNY}
	, #{ifamVaccinateNY}
)

mapper.select:
SELECT ifmmSeq , ifmmID FROM member WHERE 1=1 AND ifmmID = #{ifmmID}
AND ifmmPW = #{ifmmPW}
AND ifmmDelNy = 0
