mapper.insert:
INSERT INTO reservation (
	ifrsPlace
	, member_seq
	, ifrsLocation
	, ifrsTel
	, ifrsPetName
	, ifrsPurpose
	, ifrsHopeDate
	, ifrsRequest
	, ifrsRegDate
	, ifrsDelNy
)
VALUES (
	#{ifrsPlace}
	, #{ifmmSeq}
	, #{ifrsLocation}
	, #{ifrsTel}
	, #{ifrsPetName}
	, #{ifrsPurpose}
	, #{ifrsHopeDate}
	, #{ifrsRequest}
	, now()
	, 0
)

	SELECT last_insert_id()

mapper.update:
UPDATE reservation
SET
	ifrsPetName = #{ifrsPetName}
	, ifrsPurpose = #{ifrsPurpose}
	, ifrsHopeDate = #{ifrsHopeDate}
	, ifrsRequest = #{ifrsRequest}
	, ifrsPurpose = #{ifrsPurpose}
WHERE 1=1
AND ifrsSeq = #{ifrsSeq}
